graphql.query:
{
  products{
		id
    title
    price
		description
		images
		category {
			id
			name
			image
		}
  }
}

{
	product(id: "120") {
		title
		price
		images
		category {
			id
			name
			image
		}
	}
}

mutation {
	addProduct(
		data: {
			title: "New Product"
			price: 10
			description: "A description"
			categoryId: 1
			images: ["https://placeimg.com/640/480/any"]
		}
	) {
		title
		price
		images
		category {
			id
			name
			image
		}
	}
}

mutation {
	updateProduct(id: "120", changes: { title: "udpate" }) {
		title
		price
		images
	}
}

{
	products(title: "Generic", categoryId: 1, price_min: 100, price_max: 1000) {
		title
		price
		category {
			id
			name
		}
	}
}

mutation {
	addCategory(
		data: { name: "New Category", image: "https://placeimg.com/640/480/any" }
	) {
		id
		name
		image
	}
}

mutation {
	updateCategory(id: 1, changes: { name: "change" }) {
		id
		name
		image
	}
}

mutation {
	addUser(
		data: {
			name: "Nicolas"
			email: "nico@gmail.com"
			password: "1234"
			avatar: "https://api.lorem.space/image/face?w=150&h=220"
		}
	) {
		id
		name
		avatar
	}
}

mutation {
	updateUser(id: 1, changes: { name: "change" }) {
		id
		name
		avatar
	}
}
